import React from "react";
import { StyleSheet, View, Text, Pressable, TextInput, FlatList ,ScrollView,Image} from "react-native";

import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";

const Stack = createStackNavigator();

const images = [
  { id: 1, uri: require("./assets/1.jpg") },
  { id: 2, uri: require("./assets/2.jpg") },
  { id: 3, uri: require("./assets/3.jpg") },
  { id: 4, uri: require("./assets/4.jpg") },
  { id: 5, uri: require("./assets/5.jpg") },
];

const DATA = [
  {
    id: "bd7acbea-c1b1-46c2-aed5-3ad53abb28ba",
    title: "First Item",
  },
  {
    id: "3ac68afc-c605-48d3-a4f8-fbd91aa97f63",
    title: "Second Item",
  },
  {
    id: "58694a0f-3da1-471f-bd96-145571e29d72",
    title: "Third Item",
  },
  {
    id: "58694a03-3da1-471f-bd96-145571e29d72",
    title: "Third Item",
  },
  {
    id: "58694a04-3da1-471f-bd96-145571e29d72",
    title: "Third Item",
  },
  {
    id: "58694a05-3da1-471f-bd96-145571e29d72",
    title: "Third Item",
  },
];

const Item = ({ title }) => (
  <View style={styles.item}>
    <Text style={{fontSize:25, margin:5}}>{title}</Text>
  </View>
);

function ScreenA({ navigation }) {
  const onPressHandler = () => {
    navigation.navigate("Screen_B");
  };

  return (
    <View style={styles.body}>
      <Text style={styles.text}>Screen A</Text>
      <Pressable
        style={({ pressed }) => ({
          backgroundColor: pressed ? "#ccc" : "#aaa",
          borderRadius: 15,
        })}
        onPress={onPressHandler}
      >
        <Text style={styles.text}>Go to Screen B</Text>
      </Pressable>
    </View>
  );
}

function ScreenB({ navigation }) {

  const renderItem = ({ item }) => <Item title={item.title} />;


  const onPressHandler = () => {
    //navigation.navigate("Screen_A");
    navigation.goBack();
  };

  return (
    <View style={styles.body}>
      <Text style={styles.text}>Screen B</Text>
      <Pressable
        style={({ pressed }) => ({
          backgroundColor: pressed ? "#ccc" : "#aaa",
          borderRadius: 15,
        })}
        onPress={onPressHandler}
      >
        <Text style={styles.text}>Go back to Screen A</Text>
      </Pressable>
      <View style={{ flex: 1 }}>


      <ScrollView>
        <View style={{ height: 800, margin: 2 }}>
          <Image
            source={images[0].uri}
            style={{
              flex: 1,
              height: null,
              width: null,
              borderRadius: 10,
              margin: 2,
            }}
          />
          <Image
            source={images[0].uri}
            style={{
              flex: 1,
              height: null,
              width: null,
              borderRadius: 10,
              margin: 2,
            }}
          />
          <Text style={{ fontSize: 25, margin: 10 }}>Sersdfsdfkan Malagiç</Text>

          <FlatList style={{margin:10}}
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.id}
      />
        </View>
      </ScrollView>
    </View>
    </View>
  );
}

function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="Screen_A" component={ScreenA} />
        <Stack.Screen name="Screen_B" component={ScreenB} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  body: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  text: {
    fontSize: 40,
    fontWeight: "bold",
    margin: 10,
  },
});

export default App;
