import { StatusBar } from "expo-status-bar";
import React from "react";
import {
  StyleSheet,
  Button,
  View,
  SafeAreaView,
  Text,
  Alert,
  ScrollView,
  Image,
  TextInput,
  FlatList
} from "react-native";

const images = [
  { id: 1, uri: require("./assets/1.jpg") },
  { id: 2, uri: require("./assets/2.jpg") },
  { id: 3, uri: require("./assets/3.jpg") },
  { id: 4, uri: require("./assets/4.jpg") },
  { id: 5, uri: require("./assets/5.jpg") },
];

const DATA = [
  {
    id: "bd7acbea-c1b1-46c2-aed5-3ad53abb28ba",
    title: "First Item",
  },
  {
    id: "3ac68afc-c605-48d3-a4f8-fbd91aa97f63",
    title: "Second Item",
  },
  {
    id: "58694a0f-3da1-471f-bd96-145571e29d72",
    title: "Third Item",
  },
  {
    id: "58694a03-3da1-471f-bd96-145571e29d72",
    title: "Third Item",
  },
  {
    id: "58694a04-3da1-471f-bd96-145571e29d72",
    title: "Third Item",
  },
  {
    id: "58694a05-3da1-471f-bd96-145571e29d72",
    title: "Third Item",
  },
];

const Item = ({ title }) => (
  <View style={styles.item}>
    <Text style={{fontSize:25, margin:5}}>{title}</Text>
  </View>
);

export default function App() {
  const [text, onChangeText] = React.useState("Useless Text");
  const [number, onChangeNumber] = React.useState(null);

  const renderItem = ({ item }) => <Item title={item.title} />;

  return (
    <View style={{ flex: 1 }}>
      <View
        style={{
          position: "relative",
          left: 0,
          right: 0,
          top: 0,
          height: 70,
          backgroundColor: "blue",
          zIndex: 1000,
          elevation: 1000,
        }}
      ></View>

      <ScrollView>
        <View style={{ height: 800, margin: 10 }}>
          <Image
            source={images[0].uri}
            style={{
              flex: 1,
              height: null,
              width: null,
              borderRadius: 10,
              margin: 10,
            }}
          />
          <Image
            source={images[0].uri}
            style={{
              flex: 1,
              height: null,
              width: null,
              borderRadius: 10,
              margin: 10,
            }}
          />
          <Text style={{ fontSize: 25, margin: 10 }}>Sersdfsdfkan Malagiç</Text>
          <TextInput
            style={styles.input}
            onChangeText={onChangeNumber}
            value={number}
            placeholder="useless placeholder"
            keyboardType="numeric"
          />
          <FlatList style={{margin:10}}
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.id}
      />
        </View>
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    borderRadius: 10,
  },
});
